# Function to enter the file paths
def get_valid_path(prompt):
    while True:
        path = input(prompt)
        path = path.replace("\\", "/")  # Replace backslashes with forward slashes
        if os.path.isdir(path):
            return path
        else:
            print("Invalid path. Please try again.")


def check_gi_info(df, input_column, output_column, dictionary):
    """
    Check if values in input_column exist in dictionary keys or values.
    
    Parameters:
    df (pd.DataFrame): The input DataFrame
    input_column (str): Name of the column to check
    output_column (str): Name of the new boolean column to create
    dictionary (dict): Dictionary to check against (both keys and values)
    
    Returns:
    pd.DataFrame: Copy of the dataFrame with the new boolean column added
    """

    df_copy = df.copy()
    
    # Combine dictionary keys and values into a single set for efficient lookup
    all_dict_values = set(dictionary.keys()) | set(dictionary.values())
    
    # Check if each value in input_column is in the combined set
    df_copy[input_column] = df_copy[input_column].str.strip()
    df_copy[output_column] = df_copy[input_column].isin(all_dict_values)
    
    return df_copy

